import React, { useState } from "react";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
import "../style/slider.scss";
import { sliderItems } from "../data";

function Slider() {
  const [slideInder, setslideInder] = useState(0);
  const handleclick = () => {};
  return (
    <div className="slider-container">
      <div className="arrow" id="left" onClick={() => handleclick("left")}>
        <ArrowBackIcon />
      </div>
      <div className="wrapperr">
        {sliderItems.map((item) => {
          return (
            <div className="Slide">
              <div className="imgContainer">
                <img
                  className="img"
                  src="https://i.pinimg.com/originals/ff/8b/ed/ff8bed083605c870d11a9ef9d82bb4a8.jpg"
                  alt="sas"
                ></img>
              </div>
              <div className="containerInfo">
                <span className="title">{item.title}</span>
                <br />
                <span className="description">{item.title}</span>

                <button className="btn">CLICK ME</button>
              </div>
            </div>
          );
        })}

        <div className="arrow" id="right" onClick={() => handleclick("right")}>
          <ArrowForwardIcon />
        </div>
      </div>
    </div>
  );
}

export default Slider;
